package io.khw.common.config;import lombok.Getter;import lombok.Setter;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.context.annotation.Configuration;import java.util.Map;@Configuration@ConfigurationProperties(prefix = "api.blog")@Getter@Setterpublic class PropertiesConfig {    private String main;    private ApiProperties kakao;    private ApiProperties naver;    @Getter    @Setter    public static class ApiProperties {        private String url;        private String path;        private Map<String, String> headers;        private String queryParamKey;        private String sortParamKey;        private String pageParamKey;        private String sizeParamKey;    }}