package io.khw.common.response;import lombok.Getter;@Getterpublic class CommonResponse<T>{    private String message;    private T result;    private String status;    public CommonResponse(String status, String message, T parameter) {        this.status = status;        this.message = message;        this.result = parameter;    }    public CommonResponse(String message, T parameter) {        this.message = message;        this.result = parameter;    }    public CommonResponse(String status, String message) {        this.status = status;        this.message = message;    }}