package io.khw.common.response;import com.fasterxml.jackson.annotation.JsonInclude;import io.khw.common.constants.ApiResponseCode;import lombok.Getter;@Getterpublic class CommonResponse<T>{    private String message;    @JsonInclude(JsonInclude.Include.NON_NULL)    private T result;    private String code;    public CommonResponse(String code, String message, T parameter) {        this.code = code;        this.message = message;        this.result = parameter;    }    public CommonResponse(String message, T parameter) {        this.message = message;        this.result = parameter;    }    public CommonResponse(String code, String message) {        this.code = code;        this.message = message;    }    public CommonResponse(ApiResponseCode apiResponseCode, T parameter) {        this.code = apiResponseCode.getCode();        this.message = apiResponseCode.getMessage();        this.result = parameter;    }    public CommonResponse(ApiResponseCode apiResponseCode) {        this.code = apiResponseCode.getCode();        this.message = apiResponseCode.getMessage();    }}