package io.khw.common.response;import io.khw.common.constants.ApiResponseCode;import lombok.Getter;@Getterpublic class ErrCommonResponse{    private String errorMessage;    private String errorCode;    public ErrCommonResponse(String errorMessage, String errorCode) {        this.errorCode = errorMessage;        this.errorMessage = errorCode;    }    public ErrCommonResponse(ApiResponseCode apiResponseCode){        this.errorCode = apiResponseCode.getCode();        this.errorMessage = apiResponseCode.getMessage();    }}