package io.khw.domain.blogsearch.dto;import com.fasterxml.jackson.annotation.JsonProperty;import io.swagger.v3.oas.annotations.media.Schema;import java.util.List;import java.util.stream.Collectors;@Schema(description = "카카오 블로그 검색 API DTO")public record ApiResponseDto(        @Schema(description = "카카오 블로그 검색 정보 객체(제목, 내용등)")        @JsonProperty("documents")        List<DocumentDto> documents,        @Schema(description = "카카오 블로그 검색 정보 객체(검색 총합)")        @JsonProperty("meta")        Meta meta) {    /**     * 카카오 블로그 검색 API DTO > 공통 API DTO 객체 컨버팅 메소드     *     * @return 공통 블로그 검색 반환 API DTO 객체 반환     */    public CommonApiResponseDto toCommon() {        CommonApiResponseDto common = new CommonApiResponseDto(documents.stream().map(DocumentDto::toCommon).collect(Collectors.toList()), meta.totalCount());        return common;    }}