package io.khw.domain.blogsearch.dto;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.annotation.JsonProperty;import io.swagger.v3.oas.annotations.media.Schema;import java.time.LocalDateTime;@Schema(description = "카카오 블로그 검색 정보 객체(제목, 내용등)")public record DocumentDto(        @Schema(description = "제목", example = "테스트 블로그 제목")        @JsonProperty("title")        String title,        @Schema(description = "썸네일", example = "https://search2.kakaocdn.net/argon/130x130_85_c/9RNtFAgSAcs")        @JsonProperty("thumbnail")        String thumbnail,        @Schema(description = "블로그 이름", example = "테스트 블로그 이름")        @JsonProperty("blogname")        String blogName,        @Schema(description = "내용", example = "테스트 블로그 내용....")        @JsonProperty("contents")        String contents,        @Schema(description = "블로그 url", example = "https://search2.kakaocdn.net/")        @JsonProperty("url")        String url,        @Schema(description = "블로그 작성시각", example = "2023-03-22T02:10:00.000+09:00")        @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyy-MM-dd'T'HH:mm:ss.SSSXXX")        LocalDateTime datetime) {    /**     * 카카오 블로그 검색 정보 객체 > 공통 검색 정보 객체 변환     *     * @return 공통 검색 정보 객체 변환     */    public CommonDocumentDto toCommon() {        CommonDocumentDto common = new CommonDocumentDto(title, thumbnail, blogName, contents, url, datetime);        return common;    }}