package io.khw.domain.blogsearch.dto;import com.fasterxml.jackson.annotation.JsonFormat;import com.fasterxml.jackson.annotation.JsonProperty;import io.swagger.v3.oas.annotations.media.Schema;import java.time.LocalDate;@Schema(description = "네이버 블로그 검색 정보 객체(제목, 내용등)")public record ItemDto(        @Schema(description = "제목", example = "테스트 블로그 제목")        @JsonProperty("title")        String title,        @Schema(description = "네이버 포스팅 썸네일", example = "blog.naver.com/huni1234")        @JsonProperty("bloggerlink")        String bloggerLink,        @Schema(description = "블로그 이름", example = "테스트 블로그 이름")        @JsonProperty("bloggername")        String bloggerName,        @Schema(description = "내용", example = "테스트 블로그 내용 ....")        @JsonProperty("description")        String description,        @Schema(description = "내용", example = "https://blog.naver.com/huni1234/223047515607")        @JsonProperty("link")        String link,        @Schema(description = "작성시각", example = "20230317")        @JsonProperty("postdate")        @JsonFormat(shape = JsonFormat.Shape.STRING, pattern = "yyyyMMdd", timezone = "Asia/Seoul")        LocalDate postDate) {    /**     * 카카오 블로그 검색 정보 객체 > 공통 검색 정보 객체 변환     *     * @return 공통 검색 정보 객체 변환     */    public CommonDocumentDto toCommon() {        CommonDocumentDto common = new CommonDocumentDto(title, bloggerLink, bloggerName, description, link, postDate.atStartOfDay());        return common;    }}