package io.khw.domain.common.dto;import io.swagger.v3.oas.annotations.media.Schema;import lombok.Builder;import lombok.Getter;@Getter@Builder@Schema(description = "공통 데이터 객체 및 총합 반환 API DTO")public class ResponseApiDto<T> {    @Schema(description = "공통 데이터 객체 제네릭타입")    private T documents;    @Schema(description = "데이터 객체가 컬렉션 타입일시 총합 표출")    private long totalCount;    public ResponseApiDto(T documents, long totalCount) {        this.documents = documents;        this.totalCount = totalCount;    }}