package io.khw.domain.popularsearchkeyword.entity;import jakarta.persistence.*;import lombok.Getter;import lombok.NoArgsConstructor;import org.springframework.data.relational.core.mapping.Table;import org.springframework.data.relational.core.mapping.Column;import java.io.Serializable;@NoArgsConstructor@Getter@Table(name = "popular_search_keyword")@Entitypublic class PopularSearchKeywordEntity implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column("id")    private Long id;    @Column("keyword")    private String keyword;    @Column("search_volume")    private long searchVolume;    private PopularSearchKeywordEntity(String keyword, long searchVolume){        this.keyword = keyword;        this.searchVolume = searchVolume;    }    public static PopularSearchKeywordEntity createPopularSearchKeyWord(String keyword, long searchVolume) {        return new PopularSearchKeywordEntity(keyword, searchVolume);    }    public void incrementSearchVolume(){        this.searchVolume += 1;    }}