package io.khw.domain.stock.dto;import io.khw.common.util.FormatUtil;import lombok.AllArgsConstructor;import lombok.Getter;import java.math.BigDecimal;import java.time.LocalDateTime;@AllArgsConstructor@Getterpublic class StockPriceDeltaRankApiDto {    private Long id;    private String code;    private String name;    private String price;    private double priceDeltaPercentage;    public StockPriceDeltaRankApiDto(Long id, String code, String name, BigDecimal price, double priceDeltaPercentage){        this.id = id;        this.code = code;        this.name = name;        formatPrice(price);        this.priceDeltaPercentage = priceDeltaPercentage;    }    public void formatPrice(BigDecimal price){        this.price = FormatUtil.formatPriceToKoreanWon(price);    }}