package io.khw.domain.stock.entity;import io.khw.domain.common.entity.AuditEntity;import org.springframework.data.annotation.Id;import jakarta.persistence.*;import lombok.*;import org.springframework.data.relational.core.mapping.Column;import org.springframework.data.relational.core.mapping.Table;import java.io.Serializable;import java.math.BigDecimal;@NoArgsConstructor(access = AccessLevel.PROTECTED)@AllArgsConstructor(access = AccessLevel.PRIVATE)@Getter@Table(name = "stock")@Builder@Entitypublic class StockEntity implements Serializable {    @Id    @Column("id")    private Long id;    @Column("code")    private String code;    @Column("name")    private String name;    @Column("price")    private BigDecimal price;    @Embedded    private AuditEntity auditEntity;}