package io.khw.domain.stockflow.entity;import jakarta.persistence.*;import lombok.Getter;import lombok.NoArgsConstructor;import org.springframework.data.relational.core.mapping.Column;import org.springframework.data.relational.core.mapping.Table;import java.io.Serializable;import java.math.BigDecimal;import java.sql.Timestamp;@NoArgsConstructor@Getter@Table(name = "transaction")@Entitypublic class TransactionEntity implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column("id")    private Long id;    @Column("stock_id")    private Long stockId;    @Column("volume")    private int volume;    @Column("price")    private BigDecimal price;    @Column("transaction_time")    private Timestamp transactionTime;}