package io.khw.search.blogsearch.service.impl;import io.khw.search.blogsearch.service.BlogSearchService;import lombok.RequiredArgsConstructor;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Component;@Component@RequiredArgsConstructorpublic class BlogSearchFactory {    @Value("${api.blog.main}")    private String blogMain;    private final KakaoBlogSearchService kakaoBlogSearchService;    private final NaverBlogSearchService naverBlogSearchService;    /**     * BlogSearchService 구현체 반환 팩토리 메소드     *     * 1.appoication-common.yml에 선언된 api.blog 값에 따른 동적 구현체 생성     *   (KakaoBlogSearchService, NaverBlogSearchService)     *     * @return BlogSearchService 구현체     */    public BlogSearchService getBlogSearchService(){        BlogSearchService blogSearchService;        switch (blogMain){            case "kakao" -> blogSearchService = kakaoBlogSearchService;            case "naver" -> blogSearchService = naverBlogSearchService;            default -> blogSearchService = kakaoBlogSearchService;        }        return blogSearchService;    }}