package io.khw.search.config;import io.khw.search.blogsearch.service.BlogSearchService;import io.khw.search.blogsearch.service.impl.BlogSearchFactory;import lombok.RequiredArgsConstructor;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;@Configuration@RequiredArgsConstructorpublic class StrategyConfig {    private final BlogSearchFactory blogSearchFactory;    /**     * BlogSearchService 구현 객체 팩토리 메소드에서 생성후 Bean에 동적 등록 메소드     *     * @return BlogSearchService 구현체     */    @Bean    public BlogSearchService blogSearchService(){        return blogSearchFactory.getBlogSearchService();    }}